timeSeriesQuery.timeSeriesQueryLanguage:
fetch k8s_container::kubernetes.io/container/cpu/request_cores
| ${project_id}
| ${location}
| ${cluster_name}
| filter resource.namespace_name !~ '(kube|istio|gatekeeper|gke|gmp|gke-gmp)-system|gke-managed-filestorecsi|gke-mcs'
| every 5m
| group_by  
    [resource.project_id, resource.location, resource.cluster_name, resource.namespace_name, metadata.system_labels.top_level_controller_name, metadata.system_labels.top_level_controller_type, resource.container_name],.mean
| { best_effort_workloads:
      filter value_request_cores_mean == 0
      | row_count;
      all_workloads:
      ident
      | row_count }
| ratio
| scale '%'

fetch k8s_container::kubernetes.io/container/cpu/request_utilization
| ${project_id}
| ${location}
| ${cluster_name}
| filter resource.namespace_name !~ '(kube|istio|gatekeeper|gke|gmp)-system|gke-managed-filestorecsi|gke-mcs'
| every 5m
| group_by
    [resource.project_id, resource.location, resource.cluster_name, resource.namespace_name, metadata.system_labels.top_level_controller_name, metadata.system_labels.top_level_controller_type, resource.container_name], .max
| { filter value_request_utilization_max >= 1
| row_count
  ; ident | row_count}
| ratio
| scale '%'

fetch k8s_container::kubernetes.io/container/memory/request_utilization
| ${project_id}
| ${location}
| ${cluster_name}
| filter (resource.namespace_name !~ '(kube|istio|gatekeeper|gke|gmp)-system|gke-managed-filestorecsi|gke-mcs' && metric.memory_type == 'non-evictable')
| every 5m
| group_by
    [resource.project_id, resource.location, resource.cluster_name, resource.namespace_name, metadata.system_labels.top_level_controller_name, metadata.system_labels.top_level_controller_type, resource.container_name],.max
| { filter value_request_utilization_max >= 1
| row_count
  ; ident | row_count}
| ratio
| scale '%'

fetch k8s_scale
| metric
    'kubernetes.io/autoscaler/container/memory/per_replica_recommended_request_bytes'
    | filter resource.namespace_name !~ '(kube|istio|gatekeeper|gke|gmp|gke-gmp)-system|gke-managed-filestorecsi|gke-mcs'
    | ${project_id}
    | ${location}
    | ${cluster_name}
| group_by 14d,
    [value_per_replica_recommended_request_bytes_mean:
       max(value.per_replica_recommended_request_bytes)]
